    @InjectView(R.id.system_icons_recycler) RecyclerView recycler;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_system_icons);
        ButterKnife.inject(this);

        ActionBar toolbar = getSupportActionBar();
        if (toolbar != null) {
            toolbar.setDisplayHomeAsUpEnabled(true);
        }

        final List<Tile> dataset = populateDataset();
        GridLayoutManager layoutManager = new GridLayoutManager(this, getResources().getInteger(R.integer.grid_cell));
        layoutManager.setSpanSizeLookup(new GridLayoutManager.SpanSizeLookup() {
            @Override
            public int getSpanSize(int position) {
                return SubheaderTile.class.isInstance(dataset.get(position)) ? getResources().getInteger(R.integer.grid_cell) : 1;
            }
        });
        recycler.setLayoutManager(layoutManager);
        recycler.setAdapter(new IconsGridAdapter(dataset));
    }

